    //     setFormData(tempTemp,tempLight,tempCo2,tempHumdity)
    //   }


    return (

        <div className='container mt-5'>
        <h1>Live Data!</h1>

            <div className='form-group'>
                <p>Temperature:</p>
                <input
                    className='form-control'
                    type='temperature'
                    name='temperature'
                    value={temperature+ " °C"}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Humidity:</p>
                <input
                    className='form-control'
                    type='humidity'
                    placeholder='95%'
                    name='humidity'
                    value={humidity}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Light Intensity:</p>
                <input
                    className='form-control'
                    type='light'
                    placeholder='2000 LUX'
                    name='light'
                    value={light}
                    disabled
