import React, {useState,useEffect}from 'react';
import { Link, Navigate } from "react-router-dom";
import  {getTemperature} from '../action/update/InformationUpdate';

const Information =(props,getTemperature) =>{

    const [formData, setFormData] = useState({
    temperature: "",
    humidity: "",
    light: "",
    co2: "",
    });
    const { temperature,humidity, light,co2 } = formData;

    const [temp,setTemp]= useState()

    useEffect(() => {
        //getTemperature();
        // getLight();
        // getCo2();
        // getHumidity();
      }, [])

      function update(){
        getTemperature();
      }



    return (

        <div className='container mt-5'>
        <h1>Live Data!</h1>

            <div className='form-group'>
                <p>Temperature:</p>
                <input
                    className='form-control'
                    type='temperature'
                    name='temperature'
                    value={temp+ " °C"}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Humidity:</p>
                <input
                    className='form-control'
                    type='humidity'
                    placeholder='95%'
                    name='humidity'
                    value={humidity}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Light Intensity:</p>
                <input
                    className='form-control'
                    type='light'
                    placeholder='2000 LUX'
                    name='light'
                    value={light}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Co2</p>
                <input
                    className='form-control'
                    type='co2'
                    placeholder='600 PPM'
                    name='co2'
                    value={co2}
                    disabled
                />
            </div>
            <br/>
            <button onClick={update()}> update</button>
    </div>
    )
};

export default Information;
