    //   }, [])


    //   function update(){
    //     let tempTemp = getTemperature();
    //     let tempLight = getLight();
    //     let tempCo2 = getCo2();
    //     let tempHumdity = getHumidity();
    //     setFormData(tempTemp,tempLight,tempCo2,tempHumdity)
    //   }


    return (

        <div className='container mt-5'>
        <h1>Live Data!</h1>

            <div className='form-group'>
                <p>Temperature:</p>
                <input
                    className='form-control'
                    type='temperature'
                    name='temperature'
                    value={temperature+ " °C"}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Humidity:</p>
                <input
                    className='form-control'
                    type='humidity'
                    name='humidity'
                    value={humidity +"%"}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Light Intensity:</p>
                <input
                    className='form-control'
                    type='light'
                    name='light'
                    value={light +" LUX"}
                    disabled
                />
            </div>
            <br/>
            <div className='form-group'>
                <p>Co2</p>
                <input
                    className='form-control'
                    type='co2'
                    name='co2'
                    value={co2 +"PPM"}
                    disabled
                />
            </div>
            <br/>
            {/* <button onClick={update()}> update</button> */}
    </div>
    )
};

export default Information;
